--
-- Структура таблицы `RSS`
--

CREATE TABLE `RSS` (
  `id` int NOT NULL,
  `Title` varchar(255) NOT NULL,
  `Author` varchar(255) NOT NULL,
  `Source` longtext CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci,
  `Date` varchar(30) NOT NULL,
  `Media` varchar(255) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT NULL,
  `Text` longtext CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb3;

